const popupShow=document.querySelector(".contacts__button"),popUp=document.querySelector(".pop-up__form"),popupClose=document.querySelector(".button__esc__image"),fieldName=document.querySelector(".modal__input__name"),popupForm=document.querySelector(".pop-up__modal"),fieldEmail=document.querySelector(".modal__input__email"),fieldText=document.querySelector(".modal__input__text");popupShow.addEventListener("click",function(){popUp.classList.add("pop-up__form_show"),fieldName.focus()}),popupClose.addEventListener("click",function(){popUp.classList.remove("pop-up__form_show"),popUp.classList.remove("pop-up__form_error")}),popupForm.addEventListener("submit",function(o){fieldName.value&&fieldEmail.value&&fieldText.value||(o.preventDefault(),popUp.classList.remove("pop-up__form_error"),popUp.offsetWidth=popUp.offsetWidth,popUp.classList.add("pop-up__form_error"))}),window.addEventListener("keydown",function(o){27===o.keyCode&&popUp.classList.contains("pop-up__form_show")&&(o.preventDefault(),popUp.classList.remove("pop-up__form_show"),popUp.classList.remove("pop-up__form_error"))});